export default app=>{app.component("card-img-top",{props:["cardclass","cardstyle","imgsrc","imgalt","title","text","btntext","btnlink","btnclass","btnclick","btnmouseenter","btnmouseleave"],template:`<article :class="['card', 'h-100', cardclass]" :style="cardstyle">\n        <img :src="imgsrc" class="card-img-top" :alt="imgalt">\n        <div class="card-body d-flex flex-column justify-content-between">\n            <h5 class="card-title">{{ title }}</h5>\n            <p class="card-text">\n                <slot name="text">{{ text }}</slot>\n            </p>\n            <a :href="btnlink" @click="btnclick" @mouseenter="btnmouseenter" @mouseleave="btnmouseleave" :class="['btn', btnclass, 'mt-auto', 'align-self-start']">{{ btntext }}</a>\n        </div>\n    </article>`})};