export default app=>{app.component("input-floating",{props:{fields:{type:Array,required:true},mb:{type:String,default:"mb-3"}},template:`\n            <div v-for="field in fields" :key="field.id" :class="['form-floating', mb]">\n                <input \n                    :type="field.type" \n                    class="form-control" \n                    :name="field.name" \n                    :id="field.id" \n                    :placeholder="field.placeholder"\n                    :value="field.value"\n                    :required="field.required"\n                >\n                <label :for="field.id">{{ field.placeholder }}</label>\n            </div>\n        `})};