for(const method in window.vueMethods){if(typeof window.vueMethods[method]==="string"){window.vueMethods[method]=new Function("return "+window.vueMethods[method])()}}const app=Vue.createApp({data(){return window.vueDatas},methods:window.vueMethods});window.vueComponents.push("cookies-consent.min");const loadComponents=window.vueComponents.map((component=>import(`/js/components/${component}.js`).then((module=>{if(module.default){module.default(app)}else{console.error(`Le composant ${component} n'a pas de "default".`)}})).catch((err=>{console.error(`Erreur lors du chargement du composant ${component}:`,err)}))));Promise.all(loadComponents).then((()=>{app.mount("#app")})).catch((err=>{console.error("Erreur lors du chargement des composants:",err)}));