export default app=>{app.component("typewriter",{props:{content:{type:String,required:true},box:{type:String,default:"div"},speed:{type:Number,default:100}},template:`\n        <component :is="box" ref="typewriterText" v-html="typedContent"></component>\n      `,data(){return{typedContent:""}},mounted(){this.startTypingEffect()},methods:{startTypingEffect(){let index=0;const text=this.content;const typeCharacter=()=>{if(index<text.length){this.typedContent+=text[index];index++;setTimeout(typeCharacter,this.speed)}};typeCharacter()}}})};